import React from "react";

const Snowflake = () => {
  const snowflakes = Array.from({ length: 50 }); // Increase the number of snowflakes

  return (
    <div
      aria-hidden="true"
      className="fixed top-0 left-0 w-full h-full overflow-hidden z-50 pointer-events-none"
    >
      {snowflakes.map((_, index) => {
        const left = Math.random() * 100; // Random horizontal position
        const size = Math.random() * 1.5 + 0.5; // Random size (scale)
        const delay = Math.random() * 5; // Random animation delay
        const duration = Math.random() * 10 + 5; // Random animation duration

        return (
          <div
            key={index}
            className="absolute top-[-10%] animate-snowflakesShake"
            style={{
              left: `${left}%`,
              animationDelay: `${delay}s`,
              animationDuration: `${duration}s`,
            }}
          >
            <div
              className="text-white font-sans shadow-lg animate-snowflakesFall"
              style={{
                fontSize: `${size}rem`,
                animationDelay: `${delay}s`,
                animationDuration: `${duration}s`,
              }}
            >
              ‚ùÖ
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default Snowflake;
