import React from "react";
import Snowflake from "./components/Snowflake";

const App = () => {
  // Adjust the number of snowflakes
  const numberOfSnowflakes = 50;

  const generateSnowflakes = () => {
    return Array.from({ length: numberOfSnowflakes }).map((_, index) => {
      const left = Math.random() * 100; // Random horizontal position
      const animationDelay = Math.random() * 5; // Outer animation delay
      const innerDelay = Math.random() * 10; // Inner animation delay

      return (
        <Snowflake
          key={index}
          left={left}
          animationDelay={animationDelay}
          innerDelay={innerDelay}
        />
      );
    });
  };

  return (
    <div className="relative min-h-screen bg-blue-900 text-center text-white flex flex-col items-center justify-center overflow-hidden">
      {/* Render snowflakes */}
      <div className="snowflakes">{generateSnowflakes()}</div>

      {/* Welcome Sign */}
      <h1 className="text-4xl md:text-6xl font-bold mb-6">
        Welcome to The Magical Realm of Cartoony Chaos
      </h1>
      <p className="text-lg md:text-xl mb-8">
        Choose your mode and let the fun begin!
      </p>

      {/* Buttons */}
      <div className="flex flex-col space-y-4 md:space-y-0 md:flex-row md:space-x-6">
        <button className="bg-green-500 hover:bg-green-600 text-white font-bold py-3 px-6 rounded-lg shadow-md transition duration-300">
          Single Player
        </button>
        <button className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-3 px-6 rounded-lg shadow-md transition duration-300">
          Multiplayer
        </button>
      </div>
    </div>
  );
};

export default App;
